        return new Twig_Source("{% extends 'base.html.twig' %}

{% block title %}Article index{% endblock %}

{% block body %}
    <h1>Article index</h1>

    <table class=\"table\">
        <thead>
            <tr>
                <th>Id</th>
                <th>Title</th>
                <th>Content</th>
                <th>Slug</th>
                <th>Category</th>
                <th>Tag(s)</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for article in articles %}
            <tr>
                <td>{{ article.id }}</td>
                <td>{{ article.title }}</td>
                <td>{{ article.content }}</td>
                <td>{{ article.slug }}</td>
                <td>{{ article.category.name }}</td>
                <td>
                    {% for tag in article.tags %}
                        <a href=\"{{ path('tag_show', {id: tag.id}) }}\"><li>{{ tag.name }} </li></a>
                    {% else %}
                        <li>No tags for this article</li>
                    {% endfor %}
                </td>
                <td>
                    <a class=\"btn btn-danger\" href=\"{{ path('article_show', {'id': article.id}) }}\">show</a>
                    <a class=\"btn btn-success\" href=\"{{ path('article_edit', {'id': article.id}) }}\">edit</a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"5\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    <a href=\"{{ path('article_new') }}\">Create new</a>
{% endblock %}
